pub fn get_ip() -&gt; Vec <p>ライブラリを実行しているPCのIPアドレスを取得します。複数のNICやipv6の環境では期待した動作をしない可能性があることに留意してください。</p> <pre><code>use network_module_util::net;

println!("port: {:?}" , net::get_ip());
</code></pre>

pub fn deserialize(buf: &amp;[u8]) -&gt; T <p>[u8]型を任意の型にデシアライズします。</p> <pre><code>use network_module_util::data;

#[cfg(kani)]
struct Vec3 {
    pub x: i16,
    pub y: i16,
    pub z: i16,
}


#[cfg(kani)]
#[kani::proof]
fn check_correctness(){
    let mut serialized_vec = Vec3 {
        x: kani::any(),
        y: kani::any(),
        z: kani::any(),
    };

    let mut buf= [0; 128];
    data::serialize(&amp;serialized_vec, &amp;mut buf);
    let deserialized_vec: Vec3 = data::deserialize(&amp;buf);

    assert!(serialized_vec.x == deserialized_vec.x);
    assert!(serialized_vec.y == deserialized_vec.y);
    assert!(serialized_vec.z == deserialized_vec.z);
}
</code></pre>

